import React, { Component } from 'react';
import { connect } from 'react-redux';
import { tsThisType } from '@babel/types';

// DO NOT MODIFY THIS FILE FOR BASE MODE!

class AnimalForm extends Component {

    state = {
        species: '',
        class: '',
    }

    componentDidMount() {
        console.log('AnimalForm did mount');
    }

    // Renders the list of animals
    render() {
        return (
            <div>
                <h4>Add An Animal</h4>
                <form onSubmit={this.handleSubmit}>
                    <label>Species</label>
                    <input type='text' onchange={(event) => this.handleChangeSpecies(event)} />
                    <label>Class</label>
                    <input type='text' onchange={(event) => this.handleChangeClass(event)} />
                    <button type="submit">Submit</button>
                </form>
            </div>
        );
    }
}

// Makes our reducers available in our component
const mapReduxStateToProps = (reduxState) => ({
    reduxState
});

export default connect(mapReduxStateToProps)(AnimalForm);
